const API = process.env.REACT_APP_API_BASE || 'http://localhost:3001';

export async function getSensors({ areaId, floorId }) {
  const qs = new URLSearchParams();
  if (areaId) qs.set('areaId', areaId);
  if (floorId) qs.set('floorId', floorId);
  const r = await fetch(`${API}/api/sensors?${qs.toString()}`);
  if (!r.ok) throw new Error('Failed to load sensors');
  return await r.json();
}

export async function getSensorValues(sensorId, limit = 5) {
  const r = await fetch(`${API}/api/sensors/${sensorId}/values?limit=${limit}`);
  if (!r.ok) throw new Error('Failed to load sensor values');
  return await r.json();
}
