export default function AdminToolbar({ onAddArea, onDeleteArea, onAddSensor, onDeleteSensor, onToggleActive, onDeleteShading, adminMode, setAdminMode }) {
  return (
    <div className="toolbar" style={{flexWrap:'wrap'}}>
      <label style={{display:'flex', alignItems:'center', gap:6}}>
        <input type="checkbox" checked={adminMode} onChange={e => setAdminMode(e.target.checked)} />
        מצב מנהל
      </label>
      <button className="btn" onClick={onAddArea}>הוספת אזור</button>
      <button className="btn" onClick={onDeleteArea}>מחיקת אזור</button>
      <button className="btn" onClick={onAddSensor}>הוספת סנסור</button>
      <button className="btn" onClick={onDeleteSensor}>מחיקת סנסור</button>
      <button className="btn" onClick={onToggleActive}>טוגל פעיל לסנסור</button>
      <button className="btn" onClick={onDeleteShading}>מחיקת מערכת הצללה</button>
    </div>
  );
}
