import { useState } from 'react';
export default function SensorForm({ initial = {}, areaId, onSubmit }) {
  const [name, setName] = useState(initial.name || '');
  const [type, setType] = useState(initial.type || '');
  const [x, setX] = useState(initial.x ?? '');
  const [y, setY] = useState(initial.y ?? '');
  const [is_active, setActive] = useState(Boolean(initial.is_active ?? true));
  return (
    <form onSubmit={e => { e.preventDefault(); onSubmit({
      area_id: areaId ?? initial.area_id, name: name.trim(), type: type.trim() || null,
      x: Number(x), y: Number(y), is_active
    }); }}>
      <div className="kv" style={{marginBottom:10}}>
        <label>שם</label><input value={name} onChange={e=>setName(e.target.value)} required />
        <label>סוג</label><input value={type} onChange={e=>setType(e.target.value)} placeholder="temp/humidity/light" />
        <label>X</label><input type="number" value={x} onChange={e=>setX(e.target.value)} required />
        <label>Y</label><input type="number" value={y} onChange={e=>setY(e.target.value)} required />
        <label>פעיל</label><input type="checkbox" checked={is_active} onChange={e=>setActive(e.target.checked)} />
      </div>
      <button className="btn" type="submit">שמור</button>
    </form>
  );
}
