import React from "react";

export default function Toolbar({ tab, setTab }) {
  const Item = ({ id, label }) => (
    <button
      onClick={() => setTab(id)}
      className={"btn-tab" + (tab === id ? " active" : "")}
    >
      {label}
    </button>
  );

  return (
    <div className="toolbar" dir="rtl">
      <Item id="shading" label="מערכת הצללה" />
      <Item id="areas" label="ניהול אזורים" />
      <Item id="sensors" label="ניהול סנסורים" />
      <Item id="maps" label="מפות הקמפוס" />
    </div>
  );
}
