import { useEffect, useState } from 'react';
import './styles.css';
import Dashboard from './components/Dashboard.jsx';
import Modal from './components/Modal.jsx';
import AreaForm from './components/forms/AreaForm.jsx';
import SensorForm from './components/forms/SensorForm.jsx';
import { ToastProvider, useToasts } from './components/Toasts.jsx';
import { listAreas, createArea, updateArea, deleteArea } from './api/areas';
import { listSensors, createSensor, updateSensor, deleteSensor, setSensorActive, updateSensorPosition, getSensorValues } from './api/sensors';

function AppInner(){
  const { push } = useToasts();
  const [areas, setAreas] = useState([]);
  const [areaId, setAreaId] = useState(null);
  const [sensors, setSensors] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [adminMode, setAdminMode] = useState(false);

  // מודאלי עריכה
  const [areaModalOpen, setAreaModalOpen] = useState(false);
  const [editingArea, setEditingArea] = useState(null);

  const [sensorModalOpen, setSensorModalOpen] = useState(false);
  const [editingSensor, setEditingSensor] = useState(null);

  // דרואר סנסור
  const [selectedSensor, setSelectedSensor] = useState(null);
  const [selectedValues, setSelectedValues] = useState([]);

  useEffect(() => {
    setLoading(true);
    listAreas().then(d => { setAreas(d); if (d[0]) setAreaId(d[0].id); })
      .catch(e => { setError(e.message); push('error','טעינת אזורים נכשלה'); })
      .finally(() => setLoading(false));
  }, []);

  useEffect(() => {
    if (!areaId) { setSensors([]); return; }
    listSensors(areaId).then(setSensors)
      .catch(e => { setError(e.message); push('error','טעינת סנסורים נכשלה'); });
    // סגירת דרואר כשמחליפים אזור
    setSelectedSensor(null); setSelectedValues([]);
  }, [areaId]);

  // אזור
  function openAddArea(){ setEditingArea(null); setAreaModalOpen(true); }
  function openEditArea(){ if(!areaId) return push('error','בחר אזור'); setEditingArea(areas.find(a=>a.id===areaId)||null); setAreaModalOpen(true); }
  async function onSubmitArea(payload){
    try {
      if (editingArea){
        const upd = await updateArea(editingArea.id, payload);
        setAreas(xs => xs.map(a => a.id===upd.id ? upd : a));
        push('info','האזור עודכן');
      } else {
        const created = await createArea(payload);
        setAreas(xs => [...xs, created]); setAreaId(created.id);
        push('info','אזור נוסף');
      }
      setAreaModalOpen(false);
    } catch(e){ setError(e.message); push('error','שמירת אזור נכשלה'); }
  }
  async function onDeleteArea(){
    if (!areaId) return push('error','בחר אזור למחיקה');
    if (!confirm('למחוק את האזור?')) return;
    try{ await deleteArea(areaId); setAreas(xs=>xs.filter(a=>a.id!==areaId)); setAreaId(null); setSensors([]); push('info','האזור נמחק'); }
    catch(e){ setError(e.message); push('error','מחיקת אזור נכשלה'); }
  }

  // סנסורים
  function openAddSensor(){ if(!areaId) return push('error','בחר אזור קודם'); setEditingSensor({ x:100, y:100 }); setSensorModalOpen(true); }
  function openEditSensorFromDrawer(s){ // נפתח כשנלחץ סנסור על המפה
    if (!s) return;
    setSelectedSensor(s);
    getSensorValues(s.id, 50).then(setSelectedValues).catch(()=>setSelectedValues([]));
  }
  async function onSubmitSensor(payload){
    try{
      if (editingSensor && editingSensor.id){
        const upd = await updateSensor(editingSensor.id, payload);
        setSensors(xs => xs.map(s => s.id===upd.id ? upd : s));
        push('info','הסנסור עודכן');
      } else {
        const created = await createSensor({ ...payload, area_id: areaId });
        setSensors(xs => [...xs, created]);
        push('info','סנסור נוסף');
      }
      setSensorModalOpen(false);
      setEditingSensor(null);
    }catch(e){ setError(e.message); push('error','שמירת סנסור נכשלה'); }
  }
  async function onDeleteSensorFromDrawer(){
    if (!selectedSensor) return;
    if (!confirm('למחוק את הסנסור?')) return;
    try{ await deleteSensor(selectedSensor.id); setSensors(xs=>xs.filter(s=>s.id!==selectedSensor.id)); setSelectedSensor(null); push('info','סנסור נמחק'); }
    catch(e){ setError(e.message); push('error','מחיקה נכשלה'); }
  }
  async function onToggleActiveFromDrawer(){
    if (!selectedSensor) return;
    try{
      const upd = await setSensorActive(selectedSensor.id, !selectedSensor.is_active);
      setSensors(xs => xs.map(s => s.id===upd.id ? upd : s));
      setSelectedSensor(upd);
      push('info', `סטטוס: ${upd.is_active?'פעיל':'כבוי'}`);
    }catch(e){ setError(e.message); push('error','עדכון סטטוס נכשל'); }
  }

  // גרירה ושמירת x,y
  async function onMoveSensor(id, x, y){
    try{
      const upd = await updateSensorPosition(id, x, y);
      setSensors(xs => xs.map(s => s.id===id ? upd : s));
      if (selectedSensor && selectedSensor.id===id) setSelectedSensor(upd);
    }catch(e){ setError(e.message); push('error','עדכון מיקום נכשל'); }
  }

  return (
    <>
      <Dashboard
        areas={areas} areaId={areaId} onPickArea={setAreaId}
        sensors={sensors}
        selectedSensor={selectedSensor} selectedValues={selectedValues}
        loading={loading} error={error}
        adminMode={adminMode} setAdminMode={setAdminMode}
        onAddArea={openAddArea} onEditArea={openEditArea} onDeleteArea={onDeleteArea}
        onAddSensor={openAddSensor}
        onEditSensorFromDrawer={openEditSensorFromDrawer}
        onDeleteSensorFromDrawer={onDeleteSensorFromDrawer}
        onToggleActiveFromDrawer={onToggleActiveFromDrawer}
        onMoveSensor={onMoveSensor}
        onCloseDrawer={()=>{ setSelectedSensor(null); setSelectedValues([]); }}
      />

      <Modal open={areaModalOpen} title={editingArea ? 'עריכת אזור' : 'אזור חדש'} onClose={() => setAreaModalOpen(false)}>
        <AreaForm initial={editingArea||{}} onSubmit={onSubmitArea} />
      </Modal>

      <Modal open={sensorModalOpen} title={editingSensor?.id ? 'עריכת סנסור' : 'סנסור חדש'} onClose={() => { setSensorModalOpen(false); setEditingSensor(null); }}>
        <SensorForm initial={editingSensor||{}} areaId={areaId} onSubmit={onSubmitSensor} />
      </Modal>
    </>
  );
}

export default function App(){ return (<ToastProvider><AppInner/></ToastProvider>); }
