import AreasPanel from './AreasPanel.jsx';
import AreaMap from './AreaMap.jsx';
import ErrorBanner from './ErrorBanner.jsx';
import AdminToolbar from './AdminToolbar.jsx';

export default function Dashboard({
  areas, areaId, onPickArea,
  sensors,
  loading, error,
  adminMode, setAdminMode,
  onAddArea, onEditArea, onDeleteArea,
  onAddSensor, onEditSensor, onDeleteSensor,
  onToggleActive,
  onMoveSensor
}) {
  const area = (areas||[]).find(a => a.id === areaId) || null;

  return (
    <div className="app">
      <aside className="sidebar">
        <h1 className="title">מערכת ניהול סנסורים וקמפוס</h1>
        <div className="sub">הוספת אזורים, סנסורים, תמונות מפה וגרירת נקודות</div>

        <AdminToolbar
          adminMode={adminMode} setAdminMode={setAdminMode}
          onAddArea={onAddArea} onEditArea={onEditArea} onDeleteArea={onDeleteArea}
          onAddSensor={onAddSensor} onEditSensor={onEditSensor} onDeleteSensor={onDeleteSensor}
          onToggleActive={onToggleActive}
        />

        <AreasPanel areas={areas||[]} currentAreaId={areaId} onPickArea={onPickArea} />
      </aside>

      <main className="main">
        <div className="header">
          <div className="title">מפה</div>
          {loading ? <div className="sub">טוען...</div> : null}
        </div>
        <ErrorBanner message={error} />
        <AreaMap area={area} sensors={sensors||[]} adminMode={adminMode} onMoveSensor={onMoveSensor} />
      </main>
    </div>
  );
}
