const API = process.env.REACT_APP_API_BASE || 'http://localhost:3001';

export async function getAreas() {
  const r = await fetch(`${API}/api/areas`);
  if (!r.ok) throw new Error('failed to load areas');
  return r.json();
}
export async function createArea(payload) {
  const r = await fetch(`${API}/api/areas`, { method:'POST', headers:{'Content-Type':'application/json'}, body: JSON.stringify(payload) });
  if (!r.ok) throw new Error('failed to create area');
  return r.json();
}
export async function updateArea(id, payload) {
  const r = await fetch(`${API}/api/areas/${id}`, { method:'PUT', headers:{'Content-Type':'application/json'}, body: JSON.stringify(payload) });
  if (!r.ok) throw new Error('failed to update area');
  return r.json();
}
export async function deleteArea(id) {
  const r = await fetch(`${API}/api/areas/${id}`, { method:'DELETE' });
  if (!r.ok) throw new Error('failed to delete area');
  return r.json();
}
export async function getFloors(areaId) {
  const r = await fetch(`${API}/api/areas/${areaId}/floors`);
  if (!r.ok) throw new Error('failed to load floors');
  return r.json();
}
export async function getAreaShadingViaAreas(areaId) {
  const r = await fetch(`${API}/api/areas/${areaId}/shading`);
  if (!r.ok) throw new Error('failed to load shading');
  return r.json();
}
