import React from "react";
import ShadingPanel from "../components/ShadingPanel";

export default function HomePage() {
  return (
    <div className="container" dir="rtl">
      <header className="header">
        <div className="brand">
          <div className="logo" />
          <div>
            <h1 className="title">מערכת ניהול סנסורים וקמפוס</h1>
            <p className="subtitle">שליטה במערכות הצללה לפי אזורים - בזמן אמת</p>
          </div>
        </div>
      </header>

      <section className="kpi">
        <div className="card">
          <div className="label">מספר אזורים</div>
          <div id="kpi-areas" className="value">-</div>
        </div>
        <div className="card">
          <div className="label">פתוחים כעת</div>
          <div id="kpi-open" className="value">-</div>
        </div>
        <div className="card">
          <div className="label">שונו ידנית היום</div>
          <div id="kpi-user" className="value">-</div>
        </div>
      </section>

      <ShadingPanel onKpiUpdate={(k)=>{
        document.getElementById('kpi-areas').textContent = k.areas;
        document.getElementById('kpi-open').textContent = k.open;
        document.getElementById('kpi-user').textContent = k.user;
      }}/>

      <footer className="footer">© קמפוס חכם - פרויקט גמר</footer>
    </div>
  );
}
