export default function SensorDrawer({ sensor, values, onClose, onEdit, onDelete, onToggle }) {
  if (!sensor) return <aside className="drawer"></aside>;

  const isShade = String(sensor.type||'').toLowerCase() === 'shade';
  const shadeState = sensor.is_active ? 'פתוח' : 'סגור';
  const toggleLabel = sensor.is_active ? 'סגור' : 'פתח';

  return (
    <aside className="drawer">
      <div className="hbar">
        <h3>{sensor.name || (isShade ? 'Shading' : 'Sensor')}</h3>
        <button className="iconbtn" title="סגור" onClick={onClose}>✕</button>
      </div>

      <div className="kv" style={{marginTop:10}}>
        <div>מס׳</div><div>{sensor.id}</div>
        <div>סוג</div><div>{sensor.type || 'לא ידוע'}</div>
        <div>מיקום</div><div>{sensor.x},{sensor.y}</div>
        <div>סטטוס</div><div>{isShade ? shadeState : (sensor.is_active ? 'פעיל' : 'כבוי')}</div>
      </div>

      <div className="toolbar" style={{marginTop:12}}>
        <button className="btn" onClick={onEdit}>ערוך</button>
        <button className="btn" onClick={onToggle}>{isShade ? toggleLabel : (sensor.is_active ? 'השבת' : 'הפעל')}</button>
        <button className="btn" onClick={onDelete}>מחק</button>
      </div>

      <div className="section card" style={{marginTop:14}}>
        <div className="hbar"><h3>קריאות אחרונות</h3></div>
        {(values||[]).slice(0,20).map(v => (
          <div key={v.id} className="item" style={{justifyContent:'space-between'}}>
            <div>{v.metric} • {v.value} {v.unit}</div>
            <div className="sub">{new Date(v.recorded_at).toLocaleString()}</div>
          </div>
        ))}
        {(!values || values.length===0) && <div className="sub">אין נתונים</div>}
      </div>
    </aside>
  );
}
