import React from "react";
export default function ErrorBanner({ msg, onClose }) {
  if (!msg) return null;
  return (
    <div style={{margin:"8px 0 12px", padding:"10px 12px", border:"1px solid #ff6666", background:"#2a0f12", color:"#ffd1d1", borderRadius:10}}>
      <div style={{display:"flex", justifyContent:"space-between", gap:10}}>
        <div>שגיאה: {String(msg)}</div>
        <button onClick={onClose} className="btn-small">סגור</button>
      </div>
    </div>
  );
}
