export default function StatusBadge({ active }) {
  const ok = Number(active) === 1;
  return (
    <span
      style={{
        padding: '2px 8px',
        borderRadius: 12,
        fontSize: 12,
        fontWeight: 600,
        color: ok ? '#0f5132' : '#842029',
        background: ok ? '#d1e7dd' : '#f8d7da',
        border: `1px solid ${ok ? '#badbcc' : '#f5c2c7'}`
      }}
      title={ok ? 'פעיל' : 'לא פעיל'}
    >
      {ok ? 'פעיל' : 'לא פעיל'}
    </span>
  );
}
