import AreasPanel from './AreasPanel.jsx';
import ShadingPanel from './ShadingPanel.jsx';
import AreaMap from './AreaMap.jsx';
import ErrorBanner from './ErrorBanner.jsx';

export default function Dashboard({
  areas, areaId, onPickArea,
  floors, floorId, onPickFloor,
  sensors,
  shading, onToggleShading,
  loading, error,
}) {
  const area = (areas||[]).find(a => a.id === areaId) || null;
  const floor = (floors||[]).find(f => f.id === floorId) || null;

  return (
    <div className="app">
      <aside className="sidebar">
        <h1 className="title">מערכת סנסורים והצללה</h1>
        <div className="sub">בחר אזור ומפה, ראה סנסורים ועדכן הצללה</div>
        <AreasPanel
          areas={areas||[]} currentAreaId={areaId} onPickArea={onPickArea}
          floors={floors||[]} currentFloorId={floorId} onPickFloor={onPickFloor}
        />
        <ShadingPanel shading={shading} onToggle={onToggleShading} />
      </aside>

      <main className="main">
        <div className="header">
          <div className="title">מפה</div>
          {loading ? <div className="sub loading">טוען נתונים...</div> : null}
        </div>
        <ErrorBanner message={error} />
        <AreaMap area={area} floor={floor} sensors={sensors||[]} />
      </main>
    </div>
  );
}
