import { useEffect, useState, useRef, useMemo } from 'react';
import { getSensorValues } from '../api/sensors';

function clamp(n, min, max){ return Math.max(min, Math.min(max, n)); }

function SensorDot({ sensor, area, adminMode, onMoved, onSelect }) {
  const [hover, setHover] = useState(false);
  const [vals, setVals] = useState(null);
  const dragging = useRef(false);

  useEffect(() => {
    if (!hover) return;
    let mounted = true;
    getSensorValues(sensor.id, 5).then(d => { if (mounted) setVals(d); }).catch(()=>{});
    return () => { mounted = false; };
  }, [hover, sensor.id]);

  const w = area?.width || 1000, h = area?.height || 1000;
  const leftPct = (sensor.x / w) * 100, topPct = (sensor.y / h) * 100;
  const shade = String(sensor.type || '').toLowerCase() === 'shade';

  // מציב את הבועה למעלה כרגיל - אם קרוב לשוליים העליונים מציב למטה
  const placementStyle = useMemo(() => {
    if (topPct < 15) return { transform:'translate(-50%, 12px)' }; // מתחת
    return { transform:'translate(-50%, calc(-100% - 12px))' };    // מעל
  }, [topPct]);

  function onMouseDown(e) {
    if (!adminMode) return;
    dragging.current = true;
    e.preventDefault();
    const container = e.currentTarget.parentElement; // mapwrap
    const rect = container.getBoundingClientRect();
    function move(ev) {
      if (!dragging.current) return;
      const nx = clamp(ev.clientX - rect.left, 0, rect.width);
      const ny = clamp(ev.clientY - rect.top, 0, rect.height);
      const x = Math.round(nx / rect.width * w);
      const y = Math.round(ny / rect.height * h);
      onMoved(sensor.id, x, y);
    }
    function up(){ dragging.current = false; window.removeEventListener('mousemove', move); window.removeEventListener('mouseup', up); }
    window.addEventListener('mousemove', move);
    window.addEventListener('mouseup', up);
  }

  return (
    <div
      className="sensor"
      style={{ left: `${leftPct}%`, top: `${topPct}%`, cursor: adminMode ? 'grab' : 'pointer' }}
      onMouseEnter={() => setHover(true)} onMouseLeave={() => setHover(false)}
      onMouseDown={onMouseDown} onClick={() => onSelect(sensor)}
    >
      {/* הסמן עצמו - מסובב רק הוא כשזה shade */}
      <div className={`marker ${shade ? 'shade' : ''} ${sensor.is_active ? 'active' : ''}`} />
      <div className="sensorLabel">{sensor.name || `#${sensor.id}`}</div>

      {hover && !adminMode && (
        <div className="tooltip" style={placementStyle}>
          <div style={{fontWeight:700, marginBottom:6, textAlign:'left'}}>
            {sensor.name || `סנסור #${sensor.id}`}
          </div>
          <div className="kv">
            <div>מס׳</div><div>{sensor.id}</div>
            <div>סוג</div><div>{sensor.type || 'לא ידוע'}</div>
            <div>מיקום</div><div>{sensor.x},{sensor.y}</div>
            <div>סטטוס</div><div>{sensor.is_active ? 'פעיל':'כבוי'}</div>
          </div>
          {Array.isArray(vals) && vals.length>0 && (
            <div style={{marginTop:8}}>
              <div style={{fontWeight:600, marginBottom:4, textAlign:'left'}}>קריאות אחרונות</div>
              {vals.map(v => (
                <div key={v.id} className="sub" style={{textAlign:'left'}}>
                  {v.metric}: {v.value} {v.unit} ({new Date(v.recorded_at).toLocaleString()})
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default function AreaMap({
  area, sensors, adminMode,
  onMoveSensor,
  onSelectSensor,
  onMapSize,
  onPlaceRequest
}) {
  const img = area?.image_url || null;

  function onImgLoad(e){
    const nw = e.currentTarget.naturalWidth;
    const nh = e.currentTarget.naturalHeight;
    onMapSize && onMapSize(nw, nh);
  }

  function onMapClick(e){
    if (!adminMode || !onPlaceRequest) return;
    const rect = e.currentTarget.getBoundingClientRect();
    const nw = area?.width || rect.width;
    const nh = area?.height || rect.height;
    const x = Math.round(clamp(e.clientX - rect.left, 0, rect.width) / rect.width * nw);
    const y = Math.round(clamp(e.clientY - rect.top,  0, rect.height) / rect.height * nh);
    onPlaceRequest(x, y);
  }

  return (
    <div className="mapwrap" onClick={onMapClick}>
      {img
        ? <img className="map" src={img} alt="map" onLoad={onImgLoad} />
        : <div className="map" style={{display:'grid',placeItems:'center'}}>אין מפה מוגדרת</div>}
      {(sensors||[]).map(s => (
        <SensorDot key={s.id} sensor={s} area={area} adminMode={adminMode}
          onMoved={onMoveSensor} onSelect={onSelectSensor} />
      ))}
      <div className="legend">
        <span className="chip"><span className="dot ok"></span> פעיל</span>
        <span className="chip"><span className="dot bad"></span> כבוי</span>
        <span className="chip"><span className="dot warn"></span> הצללה</span>
      </div>
    </div>
  );
}
