import React, { useEffect, useState } from "react";
import { listAreas, createArea, updateArea, deleteArea } from "../api/areas";
import ErrorBanner from "./ErrorBanner";

export default function AreasPanel() {
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");

  const [form, setForm] = useState({ name:"", width:"", height:"", image_url:"" });
  const [editing, setEditing] = useState(null);

  const load = async () => {
    setLoading(true);
    setErr("");
    try {
      const data = await listAreas();
      setRows(Array.isArray(data) ? data : data?.data || []);
    } catch (e) {
      console.error(e);
      setErr(e.message || "listAreas failed");
      setRows([]); // לא מפיל את האפליקציה
    } finally { setLoading(false); }
  };

  useEffect(()=>{ load(); }, []);

  const onCreate = async (e) => {
    e.preventDefault();
    try {
      const payload = { 
        name: form.name.trim(),
        width: Number(form.width||0),
        height: Number(form.height||0),
        image_url: form.image_url?.trim() || ""
      };
      await createArea(payload);
      setForm({ name:"", width:"", height:"", image_url:"" });
      await load();
    } catch (e) { setErr(e.message); }
  };

  const onSave = async (row) => {
    try {
      const { id } = row;
      const payload = {
        name: row.name,
        width: Number(row.width||0),
        height: Number(row.height||0),
        image_url: row.image_url || ""
      };
      await updateArea(id, payload);
      setEditing(null);
      await load();
    } catch (e) { setErr(e.message); }
  };

  const onDelete = async (id) => {
    if (!window.confirm("למחוק אזור?")) return;
    try {
      await deleteArea(id);
      await load();
    } catch (e) { setErr(e.message); }
  };

  const setRow = (id, key, val) => {
    setRows(prev => prev.map(r => r.id === id ? { ...r, [key]: val } : r));
  };

  return (
    <section className="panel" dir="rtl">
      <h3 style={{marginTop:0}}>ניהול אזורים</h3>

      <ErrorBanner msg={err} onClose={()=>setErr("")} />

      <form onSubmit={onCreate} className="form-row" style={{marginBottom:12}}>
        <input className="input" placeholder="שם אזור" value={form.name} onChange={e=>setForm({...form,name:e.target.value})} required />
        <input className="input" placeholder="רוחב" type="number" value={form.width} onChange={e=>setForm({...form,width:e.target.value})} />
        <input className="input" placeholder="גובה" type="number" value={form.height} onChange={e=>setForm({...form,height:e.target.value})} />
        <input className="input" placeholder="קישור מפה (אופציונלי)" value={form.image_url} onChange={e=>setForm({...form,image_url:e.target.value})} />
        <button className="btn-small" type="submit">הוסף אזור</button>
      </form>

      {loading ? <div>טוען...</div> : (
        <table className="table">
          <thead>
            <tr>
              <th>שם</th><th>רוחב</th><th>גובה</th><th>מפה (image_url)</th><th>פעולות</th>
            </tr>
          </thead>
          <tbody>
            {rows.map(r => (
              <tr key={r.id}>
                <td>
                  {editing===r.id
                    ? <input className="input" value={r.name||""} onChange={e=>setRow(r.id,"name",e.target.value)} />
                    : r.name}
                </td>
                <td>
                  {editing===r.id
                    ? <input className="input" type="number" value={r.width||0} onChange={e=>setRow(r.id,"width",e.target.value)} />
                    : r.width}
                </td>
                <td>
                  {editing===r.id
                    ? <input className="input" type="number" value={r.height||0} onChange={e=>setRow(r.id,"height",e.target.value)} />
                    : r.height}
                </td>
                <td>
                  {editing===r.id
                    ? <input className="input" value={r.image_url||""} onChange={e=>setRow(r.id,"image_url",e.target.value)} />
                    : <a href={r.image_url||"#"} target="_blank" rel="noreferrer">{r.image_url? "צפה":"-"}</a>}
                </td>
                <td className="actions">
                  {editing===r.id
                    ? <>
                        <button className="btn-small" onClick={()=>onSave(r)}>שמור</button>
                        <button className="btn-small" onClick={()=>setEditing(null)}>בטל</button>
                      </>
                    : <>
                        <button className="btn-small" onClick={()=>setEditing(r.id)}>ערוך</button>
                        <button className="btn-small" onClick={()=>onDelete(r.id)}>מחק</button>
                      </>
                  }
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </section>
  );
}
