import { useEffect, useState, useRef } from 'react';
import { getSensorValues, updateSensorPosition } from '../api/sensors';

function SensorDot({ sensor, area, floor, adminMode, onMoved }) {
  const [hover, setHover] = useState(false);
  const [vals, setVals] = useState(null);
  const dragging = useRef(false);

  useEffect(() => {
    if (!hover) return;
    let mounted = true;
    getSensorValues(sensor.id, 5).then(d => { if (mounted) setVals(d); }).catch(()=>{});
    return () => { mounted = false; };
  }, [hover, sensor.id]);

  const w = (floor?.width || area?.width || 1000);
  const h = (floor?.height || area?.height || 1000);
  const leftPct = (sensor.x / w) * 100;
  const topPct  = (sensor.y / h) * 100;

  function onMouseDown(e) {
    if (!adminMode) return;
    dragging.current = true;
    e.stopPropagation();
    e.preventDefault();
    const map = e.currentTarget.parentElement; // .mapwrap
    const rect = map.getBoundingClientRect();

    function move(ev) {
      if (!dragging.current) return;
      const nx = Math.max(0, Math.min(rect.width, ev.clientX - rect.left));
      const ny = Math.max(0, Math.min(rect.height, ev.clientY - rect.top));
      const x = Math.round(nx / rect.width * w);
      const y = Math.round(ny / rect.height * h);
      onMoved(sensor.id, x, y, { previewLeft: (x/w)*100, previewTop: (y/h)*100 });
    }
    function up() {
      dragging.current = false;
      window.removeEventListener('mousemove', move);
      window.removeEventListener('mouseup', up);
    }
    window.addEventListener('mousemove', move);
    window.addEventListener('mouseup', up);
  }

  const style = { left: `${leftPct}%`, top: `${topPct}%`, cursor: adminMode ? 'grab' : 'pointer' };

  return (
    <div className={`sensor ${sensor.is_active ? 'active' : ''}`} style={style}
         onMouseEnter={() => setHover(true)} onMouseLeave={() => setHover(false)}
         onMouseDown={onMouseDown}>
      {hover && !adminMode && (
        <div className="tooltip">
          <div style={{fontWeight:700, marginBottom:6}}>{sensor.name}</div>
          <div className="kv">
            <div>סוג</div><div>{sensor.type || 'לא ידוע'}</div>
            <div>מיקום</div><div>{sensor.x},{sensor.y}</div>
            <div>סטטוס</div><div>{sensor.is_active ? 'פעיל':'כבוי'}</div>
          </div>
          {Array.isArray(vals) && vals.length>0 && (
            <div style={{marginTop:8}}>
              <div style={{fontWeight:600, marginBottom:4}}>קריאות אחרונות</div>
              {vals.map(v => (
                <div key={v.id} className="sub">
                  {v.metric}: {v.value} {v.unit} ({new Date(v.recorded_at).toLocaleString()})
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default function AreaMap({ area, floor, sensors, adminMode, onMoveSensor }) {
  const img = (floor && floor.map_url) || (area && area.image_url) || null;
  return (
    <div className="mapwrap">
      {img ? <img className="map" src={img} alt="map" /> : <div className="map" style={{display:'grid',placeItems:'center'}}>אין מפה מוגדרת</div>}
      {(sensors||[]).map(s => (
        <SensorDot key={s.id} sensor={s} area={area} floor={floor} adminMode={adminMode}
          onMoved={(id,x,y) => onMoveSensor(id,x,y)} />
      ))}
      <div className="legend">
        <span className="chip"><span className="dot ok"></span> פעיל</span>
        <span className="chip"><span className="dot bad"></span> כבוי</span>
      </div>
    </div>
  );
}
