import { useEffect, useState } from 'react';
import './styles.css';
import Dashboard from './components/Dashboard.jsx';
import { getAreas, getFloors, getAreaShadingViaAreas, createArea, deleteArea } from './api/areas';
import { getSensors, createSensor, deleteSensor, setSensorActive, updateSensorPosition } from './api/sensors';
import { getAreaShading, setAreaShading, deleteAreaShading } from './api/shading';

export default function App() {
  const [areas, setAreas] = useState([]);
  const [areaId, setAreaId] = useState(null);
  const [floors, setFloors] = useState([]);
  const [floorId, setFloorId] = useState(null);
  const [sensors, setSensors] = useState([]);
  const [shading, setShading] = useState(null);
  const [adminMode, setAdminMode] = useState(false);

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // טעינת אזורים
  useEffect(() => {
    let mounted = true;
    setLoading(true);
    getAreas()
      .then(d => { if (mounted) setAreas(d); })
      .catch(e => { if (mounted) setError(e.message); })
      .finally(() => mounted && setLoading(false));
    return () => { mounted = false; };
  }, []);

  // כשנבחר אזור נטען קומות, סנסורים, הצללה
  useEffect(() => {
    if (!areaId) { setFloors([]); setFloorId(null); setSensors([]); setShading(null); return; }
    let mounted = true;
    setLoading(true);
    Promise.all([
      getFloors(areaId).catch(()=>[]),
      getSensors({ areaId, floorId: null }).catch(()=>[]),
      getAreaShading(areaId).catch(() => getAreaShadingViaAreas(areaId).catch(()=>null)),
    ])
    .then(([fl, ss, sh]) => {
      if (!mounted) return;
      setFloors(fl || []);
      setSensors(ss || []);
      setShading(sh || null);
    })
    .catch(e => mounted && setError(e.message))
    .finally(() => mounted && setLoading(false));
    return () => { mounted = false; };
  }, [areaId]);

  // כשנבחרה קומה - נטען סנסורים מסוננים
  useEffect(() => {
    if (!areaId) return;
    let mounted = true;
    getSensors({ areaId, floorId })
      .then(d => { if (mounted) setSensors(d || []); })
      .catch(e => mounted && setError(e.message));
    return () => { mounted = false; };
  }, [areaId, floorId]);

  async function onToggleShading(status) {
    if (!areaId) return;
    try {
      const updated = await setAreaShading(areaId, status);
      setShading(updated);
    } catch (e) { setError(e.message); }
  }

  // ניהול - הוספת אזור
  async function handleAddArea() {
    const name = prompt('שם אזור?');
    if (!name) return;
    try {
      const created = await createArea({ name });
      setAreas(prev => [...prev, created]);
      if (!areaId) setAreaId(created.id);
    } catch (e) { setError(e.message); }
  }

  // ניהול - מחיקת אזור נוכחי
  async function handleDeleteArea() {
    if (!areaId) return alert('בחר אזור קודם');
    if (!confirm('בטוח למחוק את האזור?')) return;
    try {
      await deleteArea(areaId);
      setAreas(prev => prev.filter(a => a.id !== areaId));
      setAreaId(null);
    } catch (e) { setError(e.message); }
  }

  // ניהול - הוספת סנסור
  async function handleAddSensor() {
    if (!areaId) return alert('בחר אזור קודם');
    const name = prompt('שם סנסור?');
    if (!name) return;
    try {
      const created = await createSensor({ area_id: areaId, floor_id: floorId || null, name, x: 100, y: 100, is_active: 1 });
      setSensors(prev => [...prev, created]);
    } catch (e) { setError(e.message); }
  }

  // ניהול - מחיקת סנסור לפי id
  async function handleDeleteSensor() {
    const id = Number(prompt('מספר סנסור למחיקה?'));
    if (!id) return;
    try {
      await deleteSensor(id);
      setSensors(prev => prev.filter(s => s.id !== id));
    } catch (e) { setError(e.message); }
  }

  // ניהול - טוגל פעיל
  async function handleToggleActive() {
    const id = Number(prompt('מספר סנסור לטוגל פעיל?'));
    if (!id) return;
    const target = sensors.find(s => s.id === id);
    if (!target) return alert('לא נמצא סנסור כזה ברשימה');
    try {
      const updated = await setSensorActive(id, !target.is_active);
      setSensors(prev => prev.map(s => s.id === id ? updated : s));
    } catch (e) { setError(e.message); }
  }

  // ניהול - מחיקת מערכת הצללה
  async function handleDeleteShading() {
    if (!areaId) return alert('בחר אזור קודם');
    if (!confirm('בטוח למחוק את מערכת ההצללה לאזור הזה?')) return;
    try {
      await deleteAreaShading(areaId);
      setShading(null);
    } catch (e) { setError(e.message); }
  }

  // גרירת סנסור - עדכון מיקום
  async function onMoveSensor(id, x, y) {
    try {
      const updated = await updateSensorPosition(id, x, y);
      setSensors(prev => prev.map(s => s.id === id ? updated : s));
    } catch (e) { setError(e.message); }
  }

  return (
    <Dashboard
      areas={areas} areaId={areaId} onPickArea={setAreaId}
      floors={floors} floorId={floorId} onPickFloor={setFloorId}
      sensors={sensors}
      shading={shading} onToggleShading={onToggleShading}
      loading={loading} error={error}
      adminMode={adminMode} setAdminMode={setAdminMode}
      onAddArea={handleAddArea} onDeleteArea={handleDeleteArea}
      onAddSensor={handleAddSensor} onDeleteSensor={handleDeleteSensor}
      onToggleActive={handleToggleActive} onDeleteShading={handleDeleteShading}
      onMoveSensor={onMoveSensor}
    />
  );
}
