import StatusBadge from './StatusBadge.jsx';

export default function AreasPanel({ areas, currentAreaId, onPickArea, floors, currentFloorId, onPickFloor }) {
  return (
    <div className="section card">
      <div className="flex" style={{justifyContent:'space-between'}}>
        <div>אזורים</div>
        <StatusBadge ok text={`${areas?.length || 0} פעילים`} />
      </div>
      <div className="list">
        {(areas||[]).map(a => (
          <button key={a.id} className="item" onClick={() => onPickArea(a.id)}
            style={{borderColor: currentAreaId===a.id ? 'var(--accent)' : 'var(--border)'}}>
            <div style={{fontWeight:600}}>{a.name}</div>
            <div className="sub">מפה {a.width ?? '-'}x{a.height ?? '-'}</div>
          </button>
        ))}
      </div>

      {(floors && floors.length>0) && (
        <>
          <div className="flex" style={{justifyContent:'space-between', marginTop:12}}>
            <div>קומות</div>
            <div className="badge">סה״כ {floors.length}</div>
          </div>
          <div className="list">
            {floors.map(f => (
              <button key={f.id} className="item" onClick={() => onPickFloor(f.id)}
                style={{borderColor: currentFloorId===f.id ? 'var(--accent)' : 'var(--border)'}}>
                <div style={{fontWeight:600}}>{f.name}{typeof f.level==='number' ? ` (${f.level})` : ''}</div>
                <div className="sub">{f.width || '-'}x{f.height || '-'}</div>
              </button>
            ))}
          </div>
        </>
      )}
    </div>
  );
}
