import StatusBadge from './StatusBadge.jsx';

export default function ShadingPanel({ shading, onToggle }) {
  if (shading == null) return null;
  return (
    <div className="section card">
      <div className="flex" style={{justifyContent:'space-between'}}>
        <div>מערכת הצללה</div>
        <StatusBadge ok={!!shading.connected} text={shading.connected ? 'מחוברת' : 'מנותקת'} />
      </div>
      <div className="toolbar">
        <button className="btn" onClick={() => onToggle('open')}>פתח</button>
        <button className="btn" onClick={() => onToggle('closed')}>סגור</button>
      </div>
      <div className="sub" style={{marginTop:8}}>מצב נוכחי: <b>{shading.status}</b></div>
    </div>
  );
}
